import React, {useState, useEffect, useRef} from "react";
import Loading from "../components/loading";
import CustomTable from "../components/table";
import Photo from "../components/photo";
import Imagebar from "../components/imagebar";
import NumberedList from "../components/numberedList";
import NavBar from "../components/navbar";
import Display from "../components/display";
import AboutUs from "../components/aboutUs";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";

function ExercisePage(){

    const [loading, setLoading] = useState(false);
    const [scrollRef, setScrollRef] = useState(null);
    const [scrollInAboutUs, setScrollInAboutUs] = useState(false);
    const [scrollUp, setScrollUp] = useState(false);
    const [details, setDetails] = useState(null);
    const navigate = useNavigate();
    const location = useLocation();
    const realPage = useRef('workout');
    const [exerciseDetails, setExerciseDetails] = useState(null);

    useEffect(() => {
        if(location.state !== null && location.state !== undefined) setDetails(parseInt(location.state.id, 10));
        else navigate('/workout');
    }, [location.state, navigate]);

    useState(() => {
        async function getExerciseData(){
            setLoading(true);
            axios.post('http://localhost:6600/exercises/view', {exerciseId:details})
                .then((Response=>{
                    if(Response.status === 200) setExerciseDetails(Response.data.exerciseData);
                    if(Response.status === 404) navigate('/login');
                }))
                .catch(Error=>{

                })
                .finally(
                    setLoading(false)
                );
        }

        if(details !== null && details !== undefined) getExerciseData();
    }, [details]);

    useEffect(() => {
        const handleScroll = () => {
            const { scrollTop, clientHeight, scrollHeight} = document.documentElement;
            if(scrollTop+clientHeight >= scrollHeight-200){
                setScrollInAboutUs(true);
            }else{
                setScrollInAboutUs(false);
            }
        };

        window.addEventListener('scroll', handleScroll);

        return () => {
            window.removeEventListener('scroll', handleScroll);
        };

    }, []);

    useEffect(() => {
        window.scrollTo({top:0, behavior:'smooth'});
    }, [scrollUp]);

    useEffect(() => {
        window.scrollTo({top:0, behavior:'smooth'});
    }, []);

    const handleLoading = (val) => {
        setLoading(val);
    }

    const getLocation = (val) => {
        setScrollRef(val);
    }

    const handleScrollAboutUs = (val) => {
        setScrollInAboutUs(val);
    }

    const handleScrollUp = () => {
        setScrollUp(!scrollUp);
    }

    return(
        <div>
            <div style={{display:'flex', flexDirection:'column', pointerEvents:(loading)?'none':null, filter:(loading)?'blur(1px)':null}}>
                <NavBar realPage={realPage} currentPage={(!scrollInAboutUs)?'workouts':'about_us'} handleLoading={handleLoading} scrollLocation={scrollRef} handleScrollAboutUs={handleScrollAboutUs} handleScrollUp={handleScrollUp}/>
                <Display footerOn={false} handleLoading={handleLoading}>
                    <CustomTable tableData={exerciseDetails.tableData}/>  
                    <Photo mainImg={exerciseDetails.img} />
                    <Imagebar />
                    <NumberedList steps={exerciseDetails.steps}/>
                </Display>
                <AboutUs getLocation={getLocation}/>
            </div>
            <Loading right={'50%'} left={'50%'} top={'50%'} bottom={'50%'} loading={loading}/>
        </div>
    );
}

export default ExercisePage;
