import React, { useEffect, useState } from 'react';
import '../component_style/body_parts_style.css';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Img from '../images/girl-pose-sport-fitness-wallpaper-preview.jpg';

function BodyParts(props) {
    const [bodyPartsList, setBodyPartsList] = useState([]);
    const navigate = useNavigate();

    const handleBodyPartAction = async(event) => {
        const bodyPartId=event.target.closest('div').getAttribute('value');
        props.handleLoading(true);
        await axios.post(props.url, {'bodyPartId':bodyPartId}, {maxRedirects:0})
            .then(Response=>{

            })
            .catch(Error=>{

            })
            .finally(()=>{
                props.handleLoading(false);
            })
    }

    useEffect(() => {
            props.handleLoading(true);
            axios.get('http://localhost:6600/bodyParts', {maxRedirects:0})
                .then(response => {
                    props.handleLoading(false);
                    console.log(response);
                    if(response.status === 403) navigate('/login');
                    if(response.status === 200) setBodyPartsList(response.data);
                    if(response.status === 500) throw new Error('Internal server error');
                })
                .catch(error => {
                    props.handleLoading(false);
                    if(error.response && error.response.status === 403) navigate('/login');
                    if(error.response && error.response.status === 500) console.log('Internal server error');
                    console.error('Error fetching body parts:', Error);
                });
    }, []);

    return (
        <div className='main_frame_body_part'>
            <h1>{props.des}</h1>
            <div className='body_parts' style={{flexWrap:'wrap', flexDirection:'row'}}>
                {
                    bodyPartsList.map(part => (
                        <div className='item_item' value={part.bodyPartId} onClick={handleBodyPartAction}>
                            <img src={Img} alt='' />
                            <span>{part.name}</span>
                        </div>
                    ))
                }
            </div>
        </div>
    );
}

export default BodyParts;
