import React from "react";
import "../component_style/data_item.css";
import Img from "../images/wall.webp";
import { useNavigate } from "react-router-dom";
import axios from 'axios';

function DataItem(props){
    
    const navigate = useNavigate();

    const getData = async() => {
        await axios.post(`http://localhost:6600/${props.type}/view`, {exerciseId:props.id})
            .then((Response=>{
                if(Response.status === 200) navigate(`${props.type}/view#view`, {state :(Response.data.details)});
                if(Response.status === 404) navigate('/login');
            }))
            .catch(Error=>{

            })
    }
        
    return(
        <div className="data_item_frame">
            <img src={Img} alt="" />
            <span>{props.name}</span>
            <div>
                <button>{(props.type!=='exercises')?'Follow':'Add'}</button>
                <button data-id={props.id} onClick={getData}>View</button>
            </div>
        </div>
    );
}

export default DataItem;
