import React, {useState, useEffect} from "react";
import "../component_style/view_data_list_style.css";
import DataItem from "./dataItem";
import Navigation from "./navigation";

function ViewDataList(props){

    const [limit, setLimit] = useState(10);
    const [start, setStart] = useState(0);
    const [total, setTotal] = useState(0);
    const [dataList, setDataList] = useState([{id:1, name:'Push ups', img:''}, {id:1, name:'Push ups', img:''}, {id:1, name:'Push ups', img:''}, {id:1, name:'Push ups', img:''}]);
    const [sortList, setSortList] = useState([]);
    const [sort, setSort] = useState([]);
    const [submit, setSubmit] = useState([]);

    useEffect(() => {
        setSortList([{name:'Gender', data:[{val:'Male', sts:false}, {val:'Female', sts:false}]}, {name:'Gender', data:[{val:'Male', sts:false}, {val:'Female', sts:false}]}, {name:'Gender', data:[{val:'Male', sts:false}, {val:'Female', sts:false}]}]);
        setSort([{name:'Gender', val:[]}, {name:'Gender', val:[]}, {name:'Gender', val:[]}]);
    }, []);

    useEffect((Event) => {
        if(submit[0] && submit[1] !== null){
            const keyIndx = parseInt(submit[1].target.getAttribute('data-keyindx'), 10);
            const index = parseInt(submit[1].target.getAttribute('data-index'), 10);
            let newList = sortList;
            let newSort = sort;
            newList[keyIndx].data[index].sts = !newList[keyIndx].data[index].sts;
            newSort[keyIndx].val = (newList[keyIndx].data).filter(itm=>itm.sts).map(itm=>itm.val);
            setSortList(newList);
            setSort(newSort);
            setSubmit([false, null]);
        }
    }, [sort, sortList, submit]);

    const handleLabelSelect = async(Event) => {
        setSubmit([true, Event]);
    }

    console.log(props.data[0]);
    return(
        <div id="view_data_list_frame">
            <div className="filter_pnl dis_flx">
                {
                    sortList.map((key, keyIndx) => (
                        <div className="display_pnl dis_flx">
                            <span className="pnl_header_name">{key.name}</span>
                            <div className="pnl_data_pnl">
                                    {key.data.map((item, index)=> (
                                        <label data-keyindx={keyIndx} data-index={index} className={(sortList[keyIndx].data[index].sts)?"labels selected":"labels"} style={{background:(sortList[keyIndx].data[index].sts)?'rgb(33, 240, 233)':'white', cursor:'pointer'}} onClick={handleLabelSelect}>{item.val}</label>
                                    ))
                                }
                            </div>
                        </div>
                    ))
                }
            </div>
            <div className="data_pnl dis_flx">
                <div className="data_itm">
                    {
                        dataList.map(itm => (
                            <DataItem type={''} id={itm.id} name={itm.name} img={itm.img}/>
                        ))
                    }
                </div>
                <div className="data_nvg">
                    <Navigation start={start} limit={limit} total={total}/>
                </div>
            </div>
        </div>
    );
}

export default ViewDataList;
