import React from 'react'
import image1 from "../images/fitness-women-training.jpg"
import '../component_style/exercise_style.css'

function Exercise(props) {
    return (
        <div className='exercise-card'>
          <img src={image1} alt='Exercise' className='exercise-image' />
  
          <div className='exercise-name'>
              <span>{props.name}</span>
          </div>
          <div className='reps'>
            <label>No of Reps:</label>
            {
              (props.reps !== null)?
              <span>{props.reps}</span>
              :<input style={{outline:0, width:'100px', height:'20px'}} type='number' min='1' placeholder='no of reps'/>
            }
          </div>
          <div className='buttons-status'>
            <button className='view-button'>View</button>
            {(props.cancel)?<button onClick={()=>{props.handleCancel(props.id)}}>Remove</button>:null}
          </div>
        </div>
      );
}

export default Exercise;
