import React, {useState, useEffect} from "react";
import "../component_style/view_data_list_style.css";
import DataItem from "./dataItem";
import Navigation from "./navigation";

function ViewDataList(props){

    const [limit, setLimit] = useState(10);
    const [start, setStart] = useState(0);
    const [total, setTotal] = useState(0);
    const [dataList, setDataList] = useState([]);
    const [sortList, setSortList] = useState([{name:'Gender', data:[{val:'All', sts:true}, {val:'Male', sts:false}, {val:'Female', sts:false}]}, {name:'Gender', data:[{val:'All', sts:true}, {val:'Male', sts:false}, {val:'Female', sts:false}]}, {name:'Gender', data:[{val:'All', sts:true}, {val:'Male', sts:false}, {val:'Female', sts:false}]}]);
    const [sort, setSort] = useState([]);

    const handleLabelSelect = (keyIndx, index) => {
        let newList = sortList;
        newList[keyIndx].data[index].sts = !newList[keyIndx].data[index].sts;
        setSortList(newList);
    }

    return(
        <div id="view_data_list_frame">
            <div className="filter_pnl">
                <div className="display_pnl">
                    <div className="pnl_data_pnl">
                        {
                            sortList.map(item => (
                                item.data.filter(det => det.sts).map(itm =>
                                    <label className="labels">{itm.val}</label>
                                ))
                            )
                        }
                    </div>
                </div>
                {
                    sortList.map((key, keyIndx) => (
                        <div className="display_pnl">
                            <span className="pnl_header_name">{key.name}</span>
                            <div className="pnl_data_pnl">
                                    {key.data.map((item, index)=> (
                                        <label className={(sortList[keyIndx].data[index].sts)?"labels selected":"labels"} onClick={handleLabelSelect(keyIndx, index)}>{item.val}</label>
                                    ))
                                }
                            </div>
                        </div>
                    ))
                }
            </div>
            <div className="data_pnl">
                <div className="data_itm">
                    {
                        dataList.map(itm => (
                            <DataItem type={props.details[1]} name={itm.name} img={itm.img}/>
                        ))
                    }
                </div>
                <div className="data_nvg">
                    <Navigation start={start} limit={limit} total={total}/>
                </div>
            </div>
        </div>
    );
}

export default ViewDataList;
